import React, { useState, useEffect } from 'react';

function TodoApp() {
    const [todo, setTodo] = useState([]);
    const [inputValue, setInputValue] = useState('');
    const [currentTime, setCurrentTime] = useState('');

    useEffect(()=>{
        const timer = setInterval(()=>{
            setCurrentTime(getCurrentTime());
        },1000)

        return()=> {
            clearInterval(timer);
        }
    },[])


    const handleInputChange = (e) => {
    setInputValue(e.target.value);
    };

    const handleAddTodo = () => {
        if (inputValue.trim() !== '') {
        setTodo([...todo, inputValue]);
        setInputValue('');
        }
    };

    const handleDeleteTodo = (index) => {
        const updatedTodos = todo.filter((_, i) => i !== index);
        setTodo(updatedTodos);
    };

    const getCurrentTime = () => {
        const date = new Date();
        const hours = date.getHours();
        const minutes = date.getMinutes();
        const seconds = date.getSeconds();

    return `${hours}:${minutes}:${seconds}`;
    };


  return (
    <div style={styles.container}>
      <h1 style={styles.heading}>Todo App</h1>
      <div style={styles.inputContainer}>
        <input
          type="text"
          value={inputValue}
          onChange={handleInputChange}
          style={styles.input}
        />
        <button onClick={handleAddTodo} style={styles.addButton}>
          Add Todo
        </button>
      </div>
      <ul style={styles.todoList}>
        {todo.map((Singletodo, index) => (
          <li key={index} style={styles.todoItem}>
            {Singletodo}
            <button
              onClick={() => handleDeleteTodo(index)}
              style={styles.deleteButton}
            >
              Delete
            </button>
          </li>
        ))}
      </ul>
      <div style={styles.currentTime}>Current Time: {setCurrentTime}</div>
    </div>
  );
}

const styles = {
  container: {
    maxWidth: '400px',
    margin: '0 auto',
    padding: '20px',
    border: '1px solid #ccc',
    borderRadius: '5px',
    backgroundColor: '#f9f9f9',
  },
  heading: {
    textAlign: 'center',
    marginBottom: '20px',
  },
  inputContainer: {
    display: 'flex',
    marginBottom: '10px',
  },
  input: {
    flexGrow: '1',
    padding: '8px',
    border: '1px solid #ccc',
    borderRadius: '5px',
    outline: 'none',
  },
  addButton: {
    padding: '8px 12px',
    border: 'none',
    borderRadius: '5px',
    backgroundColor: '#4caf50',
    color: '#fff',
    cursor: 'pointer',
  },
  todoList: {
    listStyleType: 'none',
    padding: '0',
  },
  todoItem: {
    display: 'flex',
    justifyContent: 'space-between',
    alignItems: 'center',
    marginBottom: '5px',
    padding: '8px',
    backgroundColor: '#fff',
    border: '1px solid #ccc',
    borderRadius: '5px',
  },
  deleteButton: {
    backgroundColor: '#f44336',
  },
  currentTime: {
    textAlign: 'center',
  }
}

export default TodoApp;
