import React, { useState, useEffect } from 'react';
import "./App.css"

function TodoApp() {
    const [todo, setTodo] = useState([]);
    const [input, setInput] = useState('');
    const [currentTime, setCurrentTime] = useState('');

    useEffect(()=>{
        const timer = setInterval(()=>{
            setCurrentTime(getCurrentTime());
        },1000)

        return()=> {
            clearInterval(timer);
        }
    },[])


    const handleInputChange = (e) => {
    setInput(e.target.value);
    };

    const handleAdd = () => {
        if(input==""){
            alert("Please add a task.")
        }
        else{
        setTodo([...todo, input]);
        setInput('');
        }
    };

    const handleDeleteTodo = (index) => {
        const deleteTodo = todo.filter((todo) => todo.index !== index);
        setTodo(deleteTodo);
    };

    const getCurrentTime = () => {
        const date = new Date();
        const hours = date.getHours();
        const minutes = date.getMinutes();
        const seconds = date.getSeconds();

    return `${hours}:${minutes}:${seconds}`;
    };


return (
    <div className='container'>
        <h1 className='heading'>Todo App</h1>
        <div className='inputContainer'>
            <input type="text" value={input} onChange={handleInputChange}/>
            <button onClick={handleAdd} className='addButton'>
                Add Todo
            </button>
        </div>
        <ul className='todoList'>
        {todo.map((Singletodo, index) => (
            <li key={index} className='todoItem'>
            {Singletodo}
            <button onClick={() => handleDeleteTodo(index)} className='deleteButton'>
                Delete
            </button>
            </li>
        ))}
        </ul>
        <div className='currentTime'>Current Time: {currentTime}</div>
    </div>
    );
}

export default TodoApp;
